mapper.select:
SELECT ID AS id, CATEGORY_ID AS categoryId, TITLE AS title, CONTENT AS content, LINK AS link, COLOR AS color, IMAGE AS image, KEYWORDS AS keyWords, DESCRIPTION AS description, WEIGHT AS weight, WEIGHT_DATE AS weightDate, PUSH_PARISE AS pushParise, HITS AS hits, POSID AS posid, CUSTOM_CONTENT_VIEW AS customContentView, VIEW_CONFIG AS viewConfig, CREATE_BY AS createBy, CREATE_DATE AS createDate, UPDATE_BY AS updateBy, UPDATE_DATE AS updateDate, REMARKS AS remarks, DEL_FLAG AS delFlag, vulnerable_num AS vulnerableNum, like_num AS likeNum, interested_num AS interestedNum, zz_flag AS zzFlag FROM ARTICLE
AND category_id = #{search.categoryId}
AND title LIKE CONCAT(CONCAT('%',#{search.title}),'%')
AND keywords LIKE CONCAT(CONCAT('%',#{search.keywords}),'%')
AND create_by LIKE CONCAT(CONCAT('%',#{search.createBy}),'%')
${item.sortField} ${item.sortType}
